import React from 'react';
import styled from 'styled-components';

const ExperienceContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
`;

const Title = styled.h2`
  font-size: 2.5rem;
  color: #2c3e50;
  text-align: center;
  margin-bottom: 3rem;
`;

const Timeline = styled.div`
  position: relative;

  &::after {
    content: '';
    position: absolute;
    width: 2px;
    background-color: #e0e0e0;
    top: 0;
    bottom: 0;
    left: 50%;
    margin-left: -1px;
  }
`;

const TimelineItem = styled.div`
  padding: 10px 40px;
  position: relative;
  width: 50%;
  box-sizing: border-box;

  &:nth-child(odd) {
    left: 0;
    text-align: right;
  }

  &:nth-child(even) {
    left: 50%;
  }

  &::after {
    content: '';
    position: absolute;
    width: 16px;
    height: 16px;
    right: -8px;
    background-color: #2c3e50;
    border: 2px solid #fff;
    border-radius: 50%;
    top: 15px;
    z-index: 1;
  }

  &:nth-child(even)::after {
    left: -8px;
  }
`;

const TimelineContent = styled.div`
  padding: 20px;
  background: #fff;
  border-radius: 8px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Date = styled.span`
  font-size: 0.9rem;
  color: #7f8c8d;
  margin-bottom: 0.5rem;
  display: block;
`;

const Role = styled.h3`
  font-size: 1.2rem;
  color: #2c3e50;
  margin: 0.5rem 0;
`;

const Organization = styled.h4`
  font-size: 1.1rem;
  color: #34495e;
  margin: 0.5rem 0;
`;

const Description = styled.p`
  font-size: 1rem;
  color: #34495e;
  line-height: 1.6;
`;

const Experience = () => {
  const experiences = [
    {
      date: "2023 - Present",
      role: "Software Engineering Student",
      organization: "University of California",
      description: "Pursuing a degree in Software Engineering with focus on full-stack development and software architecture."
    },
    {
      date: "2022 - 2023",
      role: "Software Development Intern",
      organization: "Tech Solutions Inc.",
      description: "Developed and maintained web applications using React and Node.js. Collaborated with senior developers on various projects."
    },
    {
      date: "2021 - 2022",
      role: "Web Development Project Lead",
      organization: "Student Tech Club",
      description: "Led a team of students in developing websites for local non-profit organizations."
    }
  ];

  return (
    <ExperienceContainer>
      <Title>Experience</Title>
      <Timeline>
        {experiences.map((exp, index) => (
          <TimelineItem key={index}>
            <TimelineContent>
              <Date>{exp.date}</Date>
              <Role>{exp.role}</Role>
              <Organization>{exp.organization}</Organization>
              <Description>{exp.description}</Description>
            </TimelineContent>
          </TimelineItem>
        ))}
      </Timeline>
    </ExperienceContainer>
  );
};

export default Experience;
