import React from 'react';
import styled from 'styled-components';

const ExperienceSection = styled.section`
  padding: 6rem 2rem;
`;

const ExperienceContainer = styled.div`
  max-width: 1000px;
  margin: 0 auto;
`;

const SectionTitle = styled.h2`
  text-align: center;
  margin-bottom: 4rem;
  color: var(--text-primary);
`;

const ExperienceCard = styled.div`
  background: var(--bg-secondary);
  border-radius: 16px;
  padding: 2rem;
  margin-bottom: 2rem;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.05);
  transition: transform 0.2s ease;

  &:hover {
    transform: translateY(-4px);
  }
`;

const Role = styled.h3`
  color: var(--text-primary);
  margin-bottom: 0.5rem;
`;

const Company = styled.h4`
  color: var(--accent);
  font-size: 1.1rem;
  margin-bottom: 1rem;
`;

const Duration = styled.p`
  color: var(--text-secondary);
  font-size: 0.9rem;
  margin-bottom: 1rem;
`;

const Description = styled.p`
  color: var(--text-secondary);
  font-size: 1rem;
  line-height: 1.6;
`;

const Experience = () => {
  const experiences = [
    {
      role: "Senior Software Engineer",
      company: "Tech Company",
      duration: "2022 - Present",
      description: "Led development of key features for enterprise applications, mentored junior developers, and implemented best practices for code quality and testing."
    },
    {
      role: "Software Engineer",
      company: "Innovation Labs",
      duration: "2020 - 2022",
      description: "Developed and maintained full-stack applications, collaborated with cross-functional teams, and improved application performance."
    },
    {
      role: "Junior Developer",
      company: "Startup Inc",
      duration: "2018 - 2020",
      description: "Built responsive web applications, implemented new features, and worked closely with designers to ensure pixel-perfect implementations."
    }
  ];

  return (
    <ExperienceSection>
      <ExperienceContainer>
        <SectionTitle>Experience</SectionTitle>
        {experiences.map((exp, index) => (
          <ExperienceCard key={index}>
            <Role>{exp.role}</Role>
            <Company>{exp.company}</Company>
            <Duration>{exp.duration}</Duration>
            <Description>{exp.description}</Description>
          </ExperienceCard>
        ))}
      </ExperienceContainer>
    </ExperienceSection>
  );
};

export default Experience;
