import React from 'react';
import styled from 'styled-components';

const AboutSection = styled.section`
  padding: 8rem 2rem 6rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 3rem;

  @media (max-width: 768px) {
    padding: 7rem 1rem 4rem;
    gap: 2rem;
  }
`;

const ProfileImage = styled.div`
  width: 250px;
  height: 250px;
  border-radius: 50%;
  overflow: hidden;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    display: block;
  }

  @media (max-width: 768px) {
    width: 200px;
    height: 200px;
  }

  @media (max-width: 480px) {
    width: 180px;
    height: 180px;
  }
`;

const Bio = styled.div`
  max-width: 700px;
  text-align: center;
  padding: 0 2rem;

  @media (max-width: 768px) {
    padding: 0 1rem;
  }
`;

const Name = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 1.5rem;
  color: var(--text-primary);
  font-weight: 600;
  letter-spacing: -0.5px;

  @media (max-width: 768px) {
    font-size: 2rem;
    margin-bottom: 1rem;
  }
`;

const Description = styled.p`
  font-size: 1.2rem;
  color: var(--text-secondary);
  line-height: 1.8;
  margin: 0;

  @media (max-width: 768px) {
    font-size: 1.1rem;
    line-height: 1.6;
  }
`;

const AboutMe = () => {
  return (
    <AboutSection>
      <ProfileImage>
        <img src="/profile.jpg" alt="Calvin Wak" />
      </ProfileImage>
      <Bio>
        <Name>Calvin Wak</Name>
        <Description>
          I'm a passionate software engineer with a focus on building elegant, efficient, 
          and user-friendly applications. With expertise in full-stack development, 
          I enjoy tackling complex problems and creating innovative solutions that make 
          a real impact.
        </Description>
      </Bio>
    </AboutSection>
  );
};

export default AboutMe;
