import React from 'react';
import styled from 'styled-components';

const ExperienceSection = styled.section`
  padding: 4rem 0;
`;

const ExperienceContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
`;

const SectionTitle = styled.h2`
  font-size: 2rem;
  color: #333;
  margin-bottom: 3rem;
  text-align: center;
`;

const ExperienceItem = styled.div`
  display: flex;
  margin-bottom: 2rem;
  padding: 0 2rem;
`;

const DateColumn = styled.div`
  width: 200px;
  padding-right: 2rem;
  text-align: right;
`;

const ContentColumn = styled.div`
  flex: 1;
  padding-left: 2rem;
  border-left: 2px solid #ddd;
`;

const Date = styled.p`
  color: #666;
  font-weight: 500;
`;

const Role = styled.h3`
  color: #333;
  margin-bottom: 0.5rem;
`;

const Company = styled.h4`
  color: #007bff;
  margin-bottom: 1rem;
`;

const Description = styled.p`
  color: #666;
  line-height: 1.6;
`;

const Experience = () => {
  const experiences = [
    {
      date: "2022 - Present",
      role: "Senior Software Engineer",
      company: "Tech Company",
      description: "Led development of key features for enterprise applications, mentored junior developers, and implemented best practices for code quality and testing."
    },
    {
      date: "2020 - 2022",
      role: "Software Engineer",
      company: "Innovation Labs",
      description: "Developed and maintained full-stack applications, collaborated with cross-functional teams, and improved application performance."
    },
    {
      date: "2018 - 2020",
      role: "Junior Developer",
      company: "Startup Inc",
      description: "Built responsive web applications, implemented new features, and worked closely with designers to ensure pixel-perfect implementations."
    }
  ];

  return (
    <ExperienceSection>
      <ExperienceContainer>
        <SectionTitle>Experience</SectionTitle>
        {experiences.map((exp, index) => (
          <ExperienceItem key={index}>
            <DateColumn>
              <Date>{exp.date}</Date>
            </DateColumn>
            <ContentColumn>
              <Role>{exp.role}</Role>
              <Company>{exp.company}</Company>
              <Description>{exp.description}</Description>
            </ContentColumn>
          </ExperienceItem>
        ))}
      </ExperienceContainer>
    </ExperienceSection>
  );
};

export default Experience;
